import React from "react";
import Banner from "../../components/Banner/Banner";
import CustomerReviews from "../../components/CustomerReviews/CustomerReviews";
import ServiceItem from "../../components/ServiceItem/ServiceItem";
import HappyCustomers from "./../../components/HappyCustomers/HappyCustomers";

import banner from "../../images/banner.png";
import visaAssistance from "../../images/services/";

const listOfServices = [
  {
    title: "Visa Assistance",
    imageUrl: banner,
    description: "Bla Bla Bla..."
  }
];

const Home = () => {
  return (
    <div>
      <Banner />
      <div className="container">
        <h1 className="text-center text-5xl font-semibold">OUR SERVICES</h1>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 md:gap-20 py-20">
          {/* Insert service Items here... */}
          {
            listOfServices.map((serviceItem) => <ServiceItem key={serviceItem.title} { ...serviceItem } />)
          }
        </div>
      </div>
      <HappyCustomers />
      <CustomerReviews />
    </div>
  );
};

export default Home;
